          <div class="row" style="border-bottom: none;">                                <div class="member-table">                                    <div class="thead">                                        <div class="tr">                                            <div class="th">开户名</div>                                            <div class="th">开户行</div>                                            <div class="th">卡号</div>                                            <div class="th">绑定时间</div>                                            <div class="th">操作</div>                                        </div>                                    </div>                                    <div class="tbody jq-tbody"></div>                                </div>                            </div>                            <div class="row btn-action">                                <input class="btn btn-bg-blue jq-btn-card-add jq-cancelBubble" type="button" value="增加绑定">                                <input class="btn btn-bg-blue jq-btn-card-lock jq-cancelBubble" type="button" value="锁定银行卡">                            </div>                            <div class="warning">                                    <span class="color-red">银行卡锁定</span>可以增强帐户安全，推荐锁定(如：帐户被他人盗用后，由于锁定的限制，您帐户的资金不会被他人提款)</p>                                <p>银行卡锁定后，                                    <span class="color-red">不能增加新银行卡绑定，自身不能解绑和解锁银行卡，联系在线客服申请解锁</span>                                </p>                            </div>                        </div>                    </div>                </div>            </section>        </main>		<?php include('template/footer.php');?>		<?php include('template/pop-slider.php');?>		</div>		<?php include('template/script.php');?>        <script>            new PCAS("P1", "C1");        </script>        <script>            // Privity            var _bank_id = 1            var delete_key            function bankInfor() {                $.ajax({                    url: "/api/Api/setUserBankInfoForm?sess=" + localStorage.session +                        "&p=1&limit=5",                    type: "GET",                    success: function (data) {                        var _each = $(".jq-tbody");                        var _length = data.body.list.user_bank_list.length;                        if (data.status) {                            switch (data.status) {                                case 100:                                    for (i = 0; i < _length; i++) {                                        _each.append(                                            '<div class="tr">' +                                            '<div class="td" data-title="开户名">' + data.body.list                                            .user_bank_list[i].ub_account_name + '</div>' +                                            '<div class="td" data-title="开户行">' + data.body.list                                            .user_bank_list[i].ub_branch_name + '</div>' +                                            '<div class="td jq-aaa" data-title="卡号">' + data.body                                            .list.user_bank_list[i].ub_account + '</div>' +                                            '<div class="td" data-title="绑定时间">' + data.body.list                                            .user_bank_list[i].ub_add_datetime + '</div>' +                                            '<div class="td td-action" data-title="操作">' +                                            '<a class="ajax-btn-bankcard-delete" href="javascript:void(0)" data-ub-id="' +                                            data.body.list.user_bank_list[i].ub_id + '">删除</a>' +                                            '</div>' +                                            '</div>'                                        )                                    }                                    break;                            }                        }                    }                });            }            function banklock() {                $('[data-btn-true-false]').click(function () {                    var key = $(this).attr('data-btn-true-false');                    if (key == 'true') {                        $.ajax({                            url: "/api/Api/lockUserBankCard?sess=" + localStorage.session,                            type: "GET",                            success: function (data) {                                if (data.status) {                                    if(data.status ==100) {                                            alert('银行卡已锁定');                                            window.location.reload();
                                    }else									{										alert(data.message);									}                                }                            }                        });                    } else if (key == 'false') {                        $('.jq-pop-window').removeClass('active');                        $('.jq-pop-true-false').removeClass('active');                        $('.jq-pop-true-false').find('.jq-title').empty();                        $('.jq-pop-true-false').find('.jq-detail').empty();                    }                });            }            function bankdelete(num) {                $('[data-btn-true-false]').click(function () {                    var key = $(this).attr('data-btn-true-false');                    if (key == 'true') {                        $.ajax({                            url: "/api/Api/delUserBankCard?sess=" + localStorage                                .session,                            type: "POST",                            data: JSON.stringify({                                ub_id: num                            }),                            success: function (data) {                                console.log(data);                                if (data.status) {                                    switch (data.status) {                                        case 100:                                            alert('银行卡已删除');                                            delete_key.parents('.tr').remove();                                            window.location.reload();                                            break;										case "999":											 alert(data.message);										break;                                    }                                }                            }                        });                    } else if (key == 'false') {                        $('.jq-pop-window').removeClass('active');                        $('.jq-pop-true-false').removeClass('active');                        $('.jq-pop-true-false').find('.jq-title').empty();                        $('.jq-pop-true-false').find('.jq-detail').empty();                    }                });            }            $('.ajax-btn-bankcard').click(function () {                var _account_name = $('.jq-pop-bankcard').find('[name="account_name"]').val();                var _account = $('.jq-pop-bankcard').find('[name="account"]').val();                var _account_check = $('.jq-pop-bankcard').find('[name="account_check"]').val();                var _branch_name = $('.jq-pop-bankcard').find('[name="branch_name"]').val();                var _province = $('.jq-pop-bankcard').find('#province').val();                var _city = $('.jq-pop-bankcard').find('#city').val();                console.log(_account, _account_name, _branch_name, _province, _city);                if (_account == _account_check) {                    $.ajax({                        url: "/api/Api/setUserBankInfo?sess=" + localStorage.session,                        type: "POST",                        data: JSON.stringify({                            account_name: _account_name, // 帳戶名稱                            account: _account, // 銀行帳號                            bank_id: _bank_id,                            branch_name: _branch_name, // 分行名稱                            province: _province, // 省份                            city: _city // 地區                        }),                        success: function (data) {                            console.log(data)                            console.log(_bank_id)                            if (data.status) {                                switch (data.status) {                                    case 100:                                        alert(data.message);                                        _bank_id += 1;                                        window.location.reload();                                        break;                                    case '999':                                        alert(data.message);                                        break;                                }                            }                        }                    });                } else {                    alert('银行帐号不一致，请重新输入')                }            });            $('.jq-btn-card-lock').click(function () {                $('.jq-pop-window').addClass('active');                $('.jq-pop-true-false').addClass('active');                $('.jq-pop-true-false').find('.jq-title').html('锁定银行卡');                $('.jq-pop-true-false').find('.jq-detail').html(                    '银行卡锁定可增强帐户安全，锁定后不能增加新银行卡绑定，自身不能解绑和解锁银行卡，如需解锁请联系在线客服申请');                banklock();            });            $(document).on('click', '.ajax-btn-bankcard-delete', function () {                delete_key = $(this)                delete_key2 = $(this).attr('data-ub-id');                $('.jq-pop-window').addClass('active');                $('.jq-pop-true-false').addClass('active');                $('.jq-pop-true-false').find('.jq-title').html('删除讯息');                $('.jq-pop-true-false').find('.jq-detail').html('是否确认删除此笔银行卡资讯');                bankdelete(delete_key2);            });            // CashCheck            function cashpassword_check(_passwd) {                $.ajax({                    url: "/api/Api/checkMoneyPasswd?sess=" + localStorage.session,                    type: "POST",                    data: JSON.stringify({                        passwd: _passwd,                    }),                    success: function (data) {                        if (data.status) {                            switch (data.status) {                                case 100:                                    alert('驗證成功');                                    $('.jq-container-step1').hide();                                    $('.jq-container-step2').show();                                    bankInfor()                                    break;                                case '999':                                    alert('密碼驗證失敗，請重新輸入');                            }                        }                    }                });            };            $(window).load(function () {                $.ajax({                    url: "/api/Api/UserMoneyPasswd?sess=" + localStorage.session,                    type: "GET",                    success: function (data) {                        if (data.body.isSet == 0) {                            $('.jq-pop-window').addClass('active');                            $('.jq-pop-cash-password').addClass('active');                        }                    }                });            });            $('.ajax-cashpassword-submit').click(function () {                var cash_key = $('[name=cashpassword]').val();                cashpassword_check(cash_key);            });        </script></body></html>