       }                    }                });                $.ajax({                    url: "/api/Api/getUser?sess=" + localStorage.session,                    type: "GET",                    success: function (data) {                        if (data.status) {                            switch (data.status) {                                case 100:                                    var _each = $(".jq-container-step2").find('.jq-user-nam');                                    _each.html(data.body.user.u_name);                                    break;                            }                        }                    }                });                $.ajax({                    url: "/api/Api/withdrawalForm?sess=" + localStorage.session,                    type: "GET",                    success: function (data) {                        if (data.status) {                            switch (data.status) {                                case 100:                                    var _each = $(".jq-container-step2").find('.jq-balance-num');                                    _each.html(data.body.balance);                                    break;                            }                        }                    }                });            }            $('.jq-btn-withdrawal-submit').click(function () {                var _bank_key = $(".jq-container-step2").find('[name=bankcard-list]').val();                var _balance_key = $(".jq-container-step2").find('.jq-balance-num').html();                var _cash_key = $(".jq-container-step2").find('.jq-cash-num').val();                                if (_cash_key >= 100 ) {                    // if (_cash_key <= _balance_key) {                        $.ajax({                            url: "/api/Api/withdrawal?sess=" + localStorage.session,                            type: "POST",                            data: JSON.stringify({                                quota: _cash_key,                                ub_id: _bank_key                            }),                            success: function (data) {                                if (data.status) {                                    switch (data.status) {                                        case 100:                                            alert('取款成功');                                            window.location.reload();                                            break;                                        case '999':                                            alert(data.message);                                    }                                }                            }                        });                    // }else{						// alert('餘額不足');					// }                } else {                    alert('单笔最低提现金额： 100 元，最高 50000 元');                }            });            // CashCheck            function cashpassword_check(_passwd) {                $.ajax({                    url: "/api/Api/checkMoneyPasswd?sess=" + localStorage.session,                    type: "POST",                    data: JSON.stringify({                        passwd: _passwd,                    }),                    success: function (data) {                        if (data.status) {                            switch (data.status) {                                case 100:                                    alert('驗證成功');                                    $('.jq-container-step1').hide();                                    $('.jq-container-step2').show();                                    break;                                case '999':                                    alert('密碼驗證失敗，請重新輸入');                            }                        }                    }                });            };            $(window).load(function () {                $.ajax({                    url: "/api/Api/UserMoneyPasswd?sess=" + localStorage.session,                    type: "GET",                    success: function (data) {                        if (data.body.isSet == 0) {                            $('.jq-pop-window').addClass('active');                            $('.jq-pop-cash-password').addClass('active');                        }                    }                });            });            $('.ajax-cashpassword-submit').click(function () {                var cash_key = $('[name=cashpassword]').val();                cashpassword_check(cash_key);                Balance_inform();            });        </script>        </div></body></html>